CREATE OR REPLACE FUNCTION pat_obter_valor_entrada ( dt_referencia_p timestamp, cd_conta_contabil_p text, nr_seq_bem_p bigint) RETURNS bigint AS $body$
DECLARE


vl_entrada_w	double precision;


BEGIN

select	coalesce(sum(vl_original),0)
into STRICT	vl_entrada_w
from	pat_bem
where	dt_aquisicao	between  trunc(dt_referencia_p,'month') and last_day(trunc(dt_referencia_p)) + 86399/86400
and	cd_conta_contabil	= coalesce(cd_conta_contabil_p, cd_conta_contabil)
and	nr_sequencia		= coalesce(nr_seq_bem_p, nr_sequencia);

return vl_entrada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
