CREATE OR REPLACE FUNCTION pat_obter_valor_transacao ( nr_seq_bem_p bigint, cd_conta_contabil_p bigint, dt_referencia_p timestamp, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE

/*
Tipos de transacao
	A = Entrada
	B = Baixa
	N = Sem valor
	V = Venda
	T = Transferencia
*/
vl_valor_w	double precision;


BEGIN

if (ie_tipo_valor_p = 'A') then
	begin
	select	coalesce(sum(vl_original),0)
	into STRICT	vl_valor_w
	from	pat_bem
	where	dt_aquisicao	between  trunc(dt_referencia_p,'month') and last_day(trunc(dt_referencia_p)) + 86399/86400
	and	cd_conta_contabil	= coalesce(cd_conta_contabil_p, cd_conta_contabil)
	and	nr_sequencia		= coalesce(nr_seq_bem_p, nr_sequencia);
	end;

elsif (ie_tipo_valor_p = 'T') then
	begin
	select	coalesce(sum(a.vl_historico),0)
	into STRICT	vl_valor_w
	from	pat_historico_bem a,
		pat_tipo_historico b,
		pat_bem c
	where	a.nr_seq_tipo 	= b.nr_sequencia
	and	a.nr_seq_bem 		= c.nr_sequencia
	and	b.ie_transferencia	= 'S'
	and	dt_historico	between  trunc(dt_referencia_p,'month') and last_day(trunc(dt_referencia_p)) + 86399/86400
	and	cd_conta_contabil	= coalesce(cd_conta_contabil_p, cd_conta_contabil)
	and	nr_seq_bem		= coalesce(nr_seq_bem_p, nr_seq_bem);
	end;
else
	begin
	select	coalesce(sum(a.vl_historico),0)
	into STRICT	vl_valor_w
	from	pat_historico_bem a,
		pat_tipo_historico b,
		pat_bem c
	where	a.nr_seq_tipo = b.nr_sequencia
	and	a.nr_seq_bem = c.nr_sequencia
	and	b.ie_valor = ie_tipo_valor_p
	and	dt_historico	between  trunc(dt_referencia_p,'month') and last_day(trunc(dt_referencia_p)) + 86399/86400
	and	cd_conta_contabil	= coalesce(cd_conta_contabil_p, cd_conta_contabil)
	and	nr_seq_bem		= coalesce(nr_seq_bem_p, nr_seq_bem);
	end;
end if;

RETURN	vl_valor_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
