SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pat_obter_vl_deprec_acum ( nr_seq_bem_p PAT_VALOR_BEM.NR_SEQ_BEM%type, dt_start_p ACCOUNTING_FILES.DT_START%type, dt_end_p ACCOUNTING_FILES.DT_END%type ) RETURNS varchar AS $body$
DECLARE

  dt_valor_w            PAT_VALOR_BEM.DT_VALOR%type;
  vl_deprec_acum_w      PAT_VALOR_BEM.VL_DEPREC_ACUM%type;

BEGIN

<<get_max_dt_valor>>
BEGIN
vl_deprec_acum_w := 0;

SELECT  MAX(a.DT_VALOR)
INTO STRICT    dt_valor_w
FROM    PAT_VALOR_BEM a
WHERE   a.NR_SEQ_BEM = nr_seq_bem_p
AND     a.dt_valor BETWEEN trunc(dt_start_p) AND pkg_date_utils.end_of(dt_end_p, 'DAY');
exception
	when no_data_found then
		dt_valor_w := null;
END;

IF (dt_valor_w IS NOT NULL AND dt_valor_w::text <> '') THEN

	SELECT  a.VL_DEPREC_ACUM
	INTO STRICT    vl_deprec_acum_w
	FROM    PAT_VALOR_BEM a
	WHERE   NR_SEQ_BEM = nr_seq_bem_p
	AND     DT_VALOR = dt_valor_w;

END IF;

RETURN vl_deprec_acum_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
