SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pcs_obter_dados_grupamento (nr_seq_grupamento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
/*
DG - Descrição do grupamento
NR - Código do responsável
NL - Sequência da lista de colunas vinculada no grupamento
*/
BEGIN

if (nr_seq_grupamento_p IS NOT NULL AND nr_seq_grupamento_p::text <> '') then
	if (ie_opcao_p = 'DG') then

		select  substr(ds_grupamento,1,255)
		into STRICT	ds_retorno_w
		from 	pcs_grupamentos
		where 	nr_sequencia = nr_seq_grupamento_p;

	elsif (ie_opcao_p = 'NR') then

		select  substr(cd_responsavel,1,255)
		into STRICT	ds_retorno_w
		from 	pcs_grupamentos
		where 	nr_sequencia = nr_seq_grupamento_p;

	elsif (ie_opcao_p = 'NL') then

		select  substr(nr_seq_lista,1,255)
		into STRICT	ds_retorno_w
		from 	pcs_grupamentos
		where 	nr_sequencia = nr_seq_grupamento_p;
	end if;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
