CREATE OR REPLACE FUNCTION pcs_obter_dados_segmento (nr_seq_segmento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
CG - Código do grupamento 
DG - Descrição do grupamento 
*/
			 
 
ds_retorno_w		varchar(255);
nr_seq_grupamento_w	bigint;
ds_grupamento_w		varchar(255);
			

BEGIN 
 
 
select a.nr_sequencia, 
	a.ds_grupamento 
into STRICT	nr_seq_grupamento_w, 
	ds_grupamento_w 
from 	pcs_grupamentos a, 
	pcs_segmento b 
where	a.nr_sequencia = b.nr_seq_grupamento 
and	b.nr_sequencia = nr_seq_segmento_p;
 
if (ie_opcao_p = 'DG') then 
  ds_retorno_w := ds_grupamento_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
