CREATE OR REPLACE FUNCTION pcs_obter_grupamento_segmento (nr_seq_segmento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
if (nr_seq_segmento_p IS NOT NULL AND nr_seq_segmento_p::text <> '') then
	select	a.ds_grupamento
	into STRICT	ds_retorno_w
	from	pcs_grupamentos a,
			pcs_segmento b
	where	b.nr_seq_grupamento = a.nr_sequencia
	and 	b.nr_sequencia = nr_seq_segmento_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
