CREATE OR REPLACE FUNCTION pcs_obter_saldo_locais (nr_seq_relatorio_p bigint, nr_seq_item_relat_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_local_estoque_w	smallint;
ds_local_estoque_w	varchar(40);
qt_saldo_w		double precision;
ds_retorno_w		varchar(255);
ds_locais_w		varchar(255);
ds_saldos_w		varchar(255);

C01 CURSOR FOR
	SELECT	cd_local_estoque,
		ds_local_estoque,
		coalesce(qt_saldo,0)
	from	w_pcs_saldo_estoque
	where	nr_seq_relatorio = nr_seq_relatorio_p
	and	nr_seq_item_relat = nr_seq_item_relat_p
	order by cd_local_estoque;


BEGIN

ds_saldos_w := null;
ds_locais_w := null;

open c01;
loop
fetch c01 into
	cd_local_estoque_w,
	ds_local_estoque_w,
	qt_saldo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_locais_w := ds_locais_w || to_char(ds_local_estoque_w) || ';';
	ds_saldos_w := ds_saldos_w || to_char(qt_saldo_w) || ';';
	end;
end loop;
close c01;


ds_locais_w := substr(ds_locais_w,1,length(ds_locais_w) -1);

ds_saldos_w := substr(ds_saldos_w,1,length(ds_saldos_w) -1);

if (ie_opcao_p = 'L') then
	ds_retorno_w := ds_locais_w;
elsif (ie_opcao_p = 'S') then
	ds_retorno_w := ds_saldos_w;
end if;

return	ds_retorno_w;

end;
$body$
