SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pcs_obter_se_atrib_integridade (nm_tabela_p text, nm_atributo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_possui_integridade_w	varchar(1);
ie_possui_dominio_w	varchar(1);


BEGIN

select	max('R')
into STRICT	ie_possui_integridade_w
from	integridade_referencial b,
   	integridade_atributo c
where	b.nm_tabela = upper(nm_tabela_p )
and	c.nm_atributo = upper(nm_atributo_p)
and	c.nm_tabela = b.nm_tabela
and	c.nm_integridade_referencial = b.nm_integridade_referencial;

select	max('D')
into STRICT	ie_possui_dominio_w
from	tabela_atributo a
where	a.nm_tabela = upper(nm_tabela_p)
and	a.nm_atributo = upper(nm_atributo_p)
and	(cd_dominio IS NOT NULL AND cd_dominio::text <> '');

ds_retorno_w := 'N';
if (ie_possui_integridade_w IS NOT NULL AND ie_possui_integridade_w::text <> '') then
   ds_retorno_w := coalesce(ie_possui_integridade_w,'N');
elsif (ie_possui_dominio_w IS NOT NULL AND ie_possui_dominio_w::text <> '') then
   ds_retorno_w := coalesce(ie_possui_dominio_w,'N');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
