CREATE OR REPLACE FUNCTION pcs_obter_se_delegado ( nr_seq_grupamento_p bigint, nr_seq_atividade_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_delegado_w			integer := 0;
ds_retorno_w			varchar(255);
nm_usuario_delegado_w	varchar(255);
ie_existe_w				varchar(1) := 'N';


BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	select	count(*)
	into STRICT	qt_delegado_w
	from	pcs_delegacao a,
			pcs_delegacao_atividades b
	where	a.nr_sequencia = b.nr_seq_delegacao
	and		b.nr_seq_atividade = nr_seq_atividade_p
	and		b.nr_seq_grupamento = nr_seq_grupamento_p
	and		upper(obter_usuario_pf(b.cd_pessoa_delegada)) = upper(nm_usuario_p);
end if;

if (qt_delegado_w > 0) then
	ie_existe_w := 'S';
else
	ie_existe_w := 'N';
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
