CREATE OR REPLACE FUNCTION pcs_obter_se_possui_politica (nr_seq_grupamento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w	integer;
ds_retorno_w	varchar(1) := 'N';

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	pcs_politicas_grupamento
where	nr_seq_grupamento = nr_seq_grupamento_p
and ((cd_estab_regra IS NOT NULL AND cd_estab_regra::text <> '') or (nr_seq_regional IS NOT NULL AND nr_seq_regional::text <> ''));

if (qt_existe_w > 0) then
    ds_retorno_w := 'S';
else
    ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
