CREATE OR REPLACE FUNCTION pcs_obter_se_segmento_sugerido (ie_curva_reclassif_p text, nr_Seq_grupamento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_sugerida_w	varchar(1);			
qt_sugerida_w	integer;	
 
	 

BEGIN 
 
select	count(*) 
into STRICT	qt_sugerida_w 
from 	pcs_grupamentos 
where 	ie_curva_abc = ie_curva_reclassif_p 
and	nr_sequencia = nr_seq_grupamento_p;
 
if (coalesce(qt_sugerida_w,0) > 0) then 
	ie_sugerida_w:= 'S';
elsif (coalesce(qt_sugerida_w,0) = 0) then 
	ie_sugerida_w:= 'N';
end if;	
	 
return	ie_sugerida_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
