CREATE OR REPLACE FUNCTION pcs_verifica_duplicidade_lista ( nr_seq_lista_p bigint, nr_seq_coluna_p bigint, nr_seq_formula_p bigint, ds_atributo_p text, ds_cadastro_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE
									
 
qt_existe_w	integer;					
					 

BEGIN 
 
select 	count(*) 
into STRICT	qt_existe_w 
from	pcs_listas a, 
	pcs_listas_colunas b 
where 	a.nr_sequencia = b.nr_seq_lista 
and	a.nr_sequencia = nr_seq_lista_p 
and	b.nr_sequencia <> nr_seq_coluna_p 
and	((nr_seq_formula = nr_seq_formula_p AND nr_seq_formula_p IS NOT NULL AND nr_seq_formula_p::text <> '') 
or	(ds_atributo = ds_atributo_p AND ds_atributo_p IS NOT NULL AND ds_atributo_p::text <> '')	 
or	(ds_cadastro = ds_cadastro_p AND ds_cadastro_p IS NOT NULL AND ds_cadastro_p::text <> ''));
 
return	qt_existe_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
