SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pc_obter_alergia ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

			
ie_liberar_hist_saude_w		parametro_medico.ie_liberar_hist_saude%type;
ds_retorno_w			varchar(4000);


c_alergias CURSOR FOR
        SELECT	distinct coalesce(
			v.ds_ficha_tecnica,
			v.ds_dcb,
			v.ds_material,
			v.ds_dcb_mat,
			v.ds_classe_mat,
			v.ds_medic_nao_cad,
			v.ds_alergeno) ds_alergia
	from	(
			(	SELECT	
					cd_pessoa_fisica,
					dt_liberacao,
					dt_fim,
					nr_seq_tipo,
					substr(obter_desc_dcb(a.nr_seq_dcb),1,80) ds_dcb,
					CASE WHEN a.ie_nega_alergias='N' THEN substr(obter_desc_alergeno(a.nr_seq_tipo),1,80)  ELSE obter_desc_expressao(346740) END  ds_alergeno,
					substr(obter_estrut_princ_ativo(a.nr_seq_ficha_tecnica,'P'),1,2000) ds_ficha_tecnica,	
					substr(obter_desc_material(a.cd_material),1,100) ds_material,
					substr(obter_desc_dcb_mat(a.cd_material,'D'),1,80) ds_dcb_mat,
					substr(obter_desc_classe_mat(a.cd_classe_mat),1,60) ds_classe_mat,
					substr(ds_medic_nao_cad,1,100) ds_medic_nao_cad
				FROM paciente_alergia a
LEFT OUTER JOIN nivel_seguranca_alerta b ON (a.nr_seq_nivel_seg = b.nr_sequencia)
WHERE a.cd_pessoa_fisica = cd_pessoa_fisica_p and ((ie_liberar_hist_saude_w = 'N') or (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')) and coalesce(a.dt_inativacao::text, '') = '' and consiste_se_exibe_alergia(nr_seq_tipo)	= 'S' and coalesce(a.ie_alerta,'S')			= 'S' and (coalesce(dt_fim::text, '') = '' or dt_fim > clock_timestamp()) and coalesce(a.ie_nega_alergias, 'N') = 'N' and cd_pessoa_fisica = cd_pessoa_fisica_p )
	) v
	order	by ds_alergia;
		
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	coalesce(max(ie_liberar_hist_saude),'N')
	into STRICT	ie_liberar_hist_saude_w
	from	parametro_medico
	where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;	
	
	for r_c_alergias in c_alergias loop
		if (r_c_alergias.ds_alergia IS NOT NULL AND r_c_alergias.ds_alergia::text <> '')then
			ds_retorno_w := ds_retorno_w || r_c_alergias.ds_alergia || ', ';
		end if;
	end loop;

	if (substr((ds_retorno_w),1,length(ds_retorno_w)-2) is not null) then
		ds_retorno_w := substr((ds_retorno_w),1,length(ds_retorno_w)-2) || '.';
	else
		ds_retorno_w := substr((ds_retorno_w),1,length(ds_retorno_w)-2);
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
