SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pc_obter_cirurgia ( nr_atendimento_p pep_pac_ci.nr_atendimento%type, cd_pessoa_fisica_p pep_pac_ci.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


data_w timestamp := null;
ds_cirurgia_w varchar(4000) := null;



BEGIN

if ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') or (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '')) then
	  
   select min(data), ds_procedimento
   into STRICT data_w, ds_cirurgia_w
	from (	
		SELECT 	hr_inicio data, substr(obter_exame_agenda(cd_procedimento, ie_origem_proced, nr_seq_proc_interno),1,240) ds_procedimento
		from 	agenda_paciente apac, agenda age 
		where 	apac.cd_agenda = age.cd_agenda
		and 	age.cd_tipo_agenda = 1
		and 	ie_status_agenda <> 'c'
		and 	(cd_procedimento IS NOT NULL AND cd_procedimento::text <> '')
		and 	coalesce(apac.nr_cirurgia::text, '') = ''
		and 	hr_inicio > clock_timestamp()
		and (apac.nr_atendimento = nr_atendimento_p or apac.cd_pessoa_fisica = cd_pessoa_fisica_p)
		
union

		SELECT 	dt_inicio_prevista data, substr(obter_exame_agenda(cd_procedimento_princ, ie_origem_proced, nr_seq_proc_interno),1,240) ds_procedimento
		from 	cirurgia 
		where 	coalesce(dt_termino::text, '') = ''
		and 	ie_status_cirurgia not in (3,4)
		and (nr_atendimento = nr_atendimento_p or cd_pessoa_fisica = cd_pessoa_fisica_p)
	) alias15 where 
	group by ds_procedimento LIMIT 1;

end if;
	
return ds_cirurgia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
