CREATE OR REPLACE FUNCTION pc_obter_dias_int_leito ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

			
dt_entrada_unidade_w	timestamp;
qt_dias_internacao_w	bigint;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	
	select	max(dt_entrada_unidade)
	into STRICT	dt_entrada_unidade_w
	from	atend_paciente_unidade
	where	nr_atendimento = nr_atendimento_p
	and		(dt_saida_unidade IS NOT NULL AND dt_saida_unidade::text <> '');
	
	if (dt_entrada_unidade_w IS NOT NULL AND dt_entrada_unidade_w::text <> '') then
		qt_dias_internacao_w 	:= (trunc(clock_timestamp()) - trunc(dt_entrada_unidade_w));
	end if;
end if;

return qt_dias_internacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
