CREATE OR REPLACE FUNCTION pc_obter_jejum (nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE


ie_jejum_w	varchar(1) := 'N';
count_w	integer := 0;
ie_exibe_sem_lib_farm_w	varchar(1);
ie_data_lib_prescr_w	varchar(1);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	ie_exibe_sem_lib_farm_w	:= wheb_assist_pck.obterParametroFuncao(
		cd_funcao_p => 1113,
		nr_parametro_p => 236,
		cd_perfil_p => wheb_usuario_pck.get_cd_perfil(),
		nm_usuario_p => wheb_usuario_pck.get_nm_usuario(),
		cd_estabelecimento_p => wheb_usuario_pck.get_cd_estabelecimento());

  ie_data_lib_prescr_w := wheb_assist_pck.obterParametroFuncao(
		cd_funcao_p => 1113,
    nr_parametro_p => 115,
		cd_perfil_p => wheb_usuario_pck.get_cd_perfil(),
		nm_usuario_p => wheb_usuario_pck.get_nm_usuario(),
		cd_estabelecimento_p => wheb_usuario_pck.get_cd_estabelecimento());

	select count(1)
	into STRICT count_w
	from rep_jejum b,
		prescr_medica a
	where	b.nr_prescricao = a.nr_prescricao
	and	a.nr_atendimento = nr_atendimento_p
	and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_w) = 'S')
	or ((ie_exibe_sem_lib_farm_w = 'S') and (coalesce(a.ie_prescr_nutricao, 'N') = 'S')))
	and	clock_timestamp() between b.dt_inicio and coalesce(b.dt_fim, clock_timestamp())
	and	coalesce(b.ie_suspenso,'N') = 'N';

	if (count_w > 0) then
		ie_jejum_w := 'S';
	end if;

end if;

return ie_jejum_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
