CREATE OR REPLACE FUNCTION pc_obter_med_assistente ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nm_medico_assistente_w	varchar(60);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	max(cd_medico_resp)
	into STRICT 	nm_medico_assistente_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_p;

end if;

return substr(obter_nome_pf(nm_medico_assistente_w),1,60);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
