CREATE OR REPLACE FUNCTION pc_obter_nega_alergias ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2) := null;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(ie_nega_alergias)
	into STRICT 	ds_retorno_w 
	from    paciente_alergia a
	where   coalesce(a.dt_inativacao::text, '') = ''
	and     consiste_se_exibe_alergia(nr_seq_tipo)	= 'S'
	and		coalesce(a.ie_alerta,'S') = 'S'				  	
	and 	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')	
	and 	cd_pessoa_fisica = cd_pessoa_fisica_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
