CREATE OR REPLACE FUNCTION pc_obter_paciente ( cd_pessoa_fisica_p text, ie_obter_iniciais_p text default 'N') RETURNS varchar AS $body$
DECLARE

	
nm_pessoa_fisica_w	pessoa_fisica.nm_pessoa_fisica%type;
ds_retorno_w		varchar(255) := '';
	

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	substr(obter_nome_pf(cd_pessoa_fisica_p),0,255)
	into STRICT	nm_pessoa_fisica_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	
	if (ie_obter_iniciais_p = 'S') then
		ds_retorno_w := obter_iniciais_nome(cd_pessoa_fisica_p, nm_pessoa_fisica_w);
	else
		ds_retorno_w := nm_pessoa_fisica_w;
	end if;

end if;

return ds_retorno_w;

end;
$body$
