CREATE OR REPLACE FUNCTION pc_obter_prev_alta ( nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS ATEND_PREVISAO_ALTA.DT_PREVISTO_ALTA%TYPE AS $body$
DECLARE


dt_prev_alta_w	atend_previsao_alta.dt_previsto_alta%type;


BEGIN

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

		select  dt_previsto_alta
		into STRICT    dt_prev_alta_w
		from    atend_previsao_alta a 
		where   a.dt_atualizacao = (SELECT  max(b.dt_atualizacao) from atend_previsao_alta b where	b.nr_atendimento = a.nr_atendimento and (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
		and     coalesce(b.ie_situacao,'A') = 'A')
		and     a.nr_atendimento = nr_atendimento_p
		and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    and     coalesce(a.ie_situacao,'A') = 'A';
		
	else

		dt_prev_alta_w := null;
		
	end if;

return dt_prev_alta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
