CREATE OR REPLACE FUNCTION pc_obter_reserva_leito (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);

BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

        select  max(substr(obter_valor_dominio(1410,x.IE_TIPO_VAGA),1,100))
	into STRICT	ds_retorno_w
	from	gestao_vaga x
	where	x.nr_atendimento = nr_atendimento_p;
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
