SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pc_obter_term_cir (nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE


ie_term_cir_w varchar(1) := 'N';
count_w	integer := 0;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select count(a.ie_tipo_consentimento)
	into STRICT count_w
	from  pep_pac_ci a,
		  agenda_paciente b
	where a.nr_seq_agenda = b.nr_sequencia
	and b.cd_pessoa_fisica = ( SELECT cd_pessoa_fisica
							   from atendimento_paciente
							   where nr_atendimento = nr_atendimento_p)
	and a.ie_tipo_consentimento = 'C';
	
	if (count_w = 0) then
		select count(*)
		into STRICT count_w
		from  pep_pac_ci a,
			  cirurgia b
		where a.nr_atendimento = b.nr_atendimento
		and b.nr_atendimento = nr_atendimento_p
		and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and a.ie_tipo_consentimento = 'C'
		and a.dt_liberacao < b.dt_termino
		and a.dt_liberacao > (SELECT max(dt_inicio_prevista)
							  from cirurgia
							  where nr_atendimento = nr_atendimento_p
							  and nr_cirurgia != b.nr_cirurgia);
		
		if (count_w > 0) then
			ie_term_cir_w := 'S';
		end if;
	else
		ie_term_cir_w := 'S';
	end if;
end if;

return ie_term_cir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
