SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pc_obter_tev ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_possuio_tev_w	varchar(1);
qt_hora_w			bigint;
	

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	
	select 	max(qt_hora)
	into STRICT   	qt_hora_w
	from	PAN_CONFIGURACAO_ESCALA a,
			PAN_CONFIGURACAO_SETOR b
	where	nr_seq_escala = 57 -- ie.escala da TEV
	and     a.NR_SEQ_SETOR = b.NR_SEQUENCIA
	and     b.CD_SETOR_ATENDIMENTO = cd_setor_atendimento_p;

	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	qt_possuio_tev_w
	from	escala_tev
	where	nr_atendimento	= nr_atendimento_p
	and		coalesce(dt_inativacao::text, '') = ''
	and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and		coalesce(ie_situacao, 'A') = 'A'
	and		clock_timestamp() between DT_AVALIACAO and DT_AVALIACAO + (coalesce(qt_hora_w,24)/24);

end if;

return qt_possuio_tev_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
