CREATE OR REPLACE FUNCTION pc_obter_ultima_atualizacao (nm_usuario_p usuario.nm_usuario%type) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w        usuario.cd_estabelecimento%type;
ds_lang_w                   varchar(255);
ds_mask_w                   varchar(255);
ds_tempo_w                  varchar(255);
ds_descricao_w              varchar(400);
ds_retorno_w                varchar(255);


BEGIN

cd_estabelecimento_w    := obter_estab_usuario(nm_usuario_p);
ds_lang_w               := pkg_date_formaters.getUserLanguageTag(cd_estabelecimento_w, nm_usuario_p);
ds_mask_w               := pkg_date_formaters.localize_mask('shortTime',ds_lang_w);

ds_tempo_w  := to_char(clock_timestamp(), ds_mask_w);

select  obter_desc_expressao(630513)
into STRICT    ds_descricao_w
;

ds_retorno_w := ds_descricao_w || ': ' ||ds_tempo_w;

return ds_retorno_w;

exception
when others then
    return '';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
