CREATE OR REPLACE FUNCTION pepo_obter_doc_mensagem ( nr_cirurgia_p pepo_doc_status.nr_cirurgia%type, nr_seq_pepo_p pepo_doc_status.nr_seq_pepo%type) RETURNS varchar AS $body$
DECLARE

    ds_mensagem_w       varchar(2000);
    cd_item_w           pepo_doc_status.cd_item%type;

    c01 CURSOR FOR
        SELECT distinct cd_item
        from    pepo_doc_status
        where   ie_status <> 'C'
        and     cd_item <> 4
        and (nr_cirurgia = nr_cirurgia_p or nr_seq_pepo = nr_seq_pepo_p);

BEGIN
    open c01;
    loop
        fetch c01
        into cd_item_w;
        EXIT WHEN NOT FOUND; /* apply on c01 */
          ds_mensagem_w := substr(ds_mensagem_w || '- ' || obter_valor_dominio(10296, cd_item_w) || ';' || chr(13), 1, 2000);
    end loop;
    close c01;
    ds_mensagem_w := CONCAT(CHR(13), ds_mensagem_w);
    return ds_mensagem_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
