CREATE OR REPLACE FUNCTION pepo_obter_doc_status_tags ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint) RETURNS varchar AS $body$
DECLARE


                            
ds_retorno_w		varchar(2000);
cd_item_w	        PEPO_DOC_STATUS.cd_item%type;
ie_status_w	        PEPO_DOC_STATUS.ie_status%type;


C01 CURSOR FOR
    SELECT cd_item,
    ie_status
    from pepo_doc_status
    where (coalesce(nr_cirurgia, 0) = coalesce(nr_cirurgia_p, 0) and (coalesce(nr_seq_pepo, 0) = coalesce(nr_seq_pepo_p, 0)))
    order by cd_item;


BEGIN
    ds_retorno_w  := null;
    open C01;
    loop
    fetch C01 into
        cd_item_w,
        ie_status_w;
    EXIT WHEN NOT FOUND; /* apply on C01 */
        if (coalesce(cd_item_w::text, '') = '') then
            ds_retorno_w  := null;
        else
            ds_retorno_w :=    substr(ds_retorno_w  || pepo_obter_objeto_doc(cd_item_w)   || '|' || ie_status_w   || '|' || Obter_Valor_Dominio(10296, cd_item_w) || ';', 1, 2000);

        end if;
    end loop;
    close C01;

    return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
