CREATE OR REPLACE FUNCTION pepo_obter_pasta_ini_item_js ( nr_seq_item_pepo_p bigint, cd_perfil_p bigint ) RETURNS bigint AS $body$
DECLARE



ds_retorno_w	bigint := 0;


BEGIN


if (nr_seq_item_pepo_p IS NOT NULL AND nr_seq_item_pepo_p::text <> '') then
	begin

	select	a.nr_seq_dic_obj
	into STRICT	ds_retorno_w
	from	perfil_item_pepo b,
		prontuario_pasta a
	where	a.nr_sequencia = b.nr_seq_pasta_inicial
	and	b.cd_perfil = cd_perfil_p
	and	coalesce(nr_seq_pasta_superior::text, '') = ''
	and	b.nr_seq_item_pepo = nr_seq_item_pepo_p
	and	a.ie_situacao = 'A';

	end;
end if;

return	ds_retorno_w;

end;
$body$
