CREATE OR REPLACE FUNCTION pepo_obter_template_lib ( nr_seq_item_pepo_p bigint ) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_template_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	perfil_item_pepo
where	nr_seq_item_pepo	=	nr_seq_item_pepo_p
and	cd_perfil		=	obter_perfil_ativo;

if (nr_sequencia_w > 0) then
	select	max(nr_seq_template)
	into STRICT	nr_seq_template_w
	from	perfil_item_pepo_template
	where	nr_seq_item_perfil	=	nr_sequencia_w;
end if;

return	nr_seq_template_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
