CREATE OR REPLACE FUNCTION pep_nr_atendimento_gerar ( nr_atendimento_p bigint, cd_pessoa_p text, dt_referencia_p timestamp, ie_tipo_atend_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE

					 
nr_atend_retorno_w 	bigint := nr_atendimento_p;
ie_forma_atend_w	varchar(5) := '';


BEGIN 
ie_forma_atend_w := coalesce(obter_valor_param_usuario(281, 247, Obter_perfil_ativo, nm_usuario_p, 0),'LV');
 
if (ie_forma_atend_w = 'LV') then 
	begin 
	 
	select 	Obter_atend_laudo_vigente(cd_pessoa_p, dt_referencia_p, 1) 
	into STRICT	nr_atend_retorno_w 
	;
	 
	end;
elsif (ie_forma_atend_w = 'A') then 
	begin 
	 
	nr_atend_retorno_w := nr_atendimento_p;	
	 
	end;
elsif (ie_forma_atend_w = 'AI') and (ie_tipo_atend_p = '1') then 
	begin 
	 
	nr_atend_retorno_w := nr_atendimento_p;	
	 
	end;
elsif (ie_forma_atend_w = 'VA') then 
	begin 
	 
	select 	coalesce(Obter_ult_laudo_vigente(cd_pessoa_p, dt_referencia_p),obter_ultimo_atendimento(cd_pessoa_p)) 
	into STRICT	nr_atend_retorno_w 
	;
	 
	end;
elsif (ie_forma_atend_w = 'VAA') then 
	begin 
	 
	select obter_atend_laudo_vigente(cd_pessoa_p,dt_referencia_p,1,'S','S') 
	into STRICT	nr_atend_retorno_w	 
	;
	 
	end;	
end if;
 
return nr_atend_retorno_w;
 
end;
$body$
