BEGIN

ds_retorno_w	:= ds_tipo_p;

if (nr_seq_topografia_p IS NOT NULL AND nr_seq_topografia_p::text <> '') or (ie_lado_p IS NOT NULL AND ie_lado_p::text <> '') then
