SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pep_obter_doc_score_flex_ii ( ie_escala_p bigint, nr_seq_score_flex_p bigint, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_retorno_flex_w	varchar(4000);


BEGIN


Select 	max(substr(obter_desc_expressao(cd_exp_informacao,obter_valor_dominio(1799, ie_escala_p)),1,4000))
into STRICT	ds_retorno_w
FROM 	escala_documentacao
WHERE 	ie_escala = ie_escala_p;



if (upper(nm_tabela_p) = 'ESCALA_EIF_II') then


	Select  max(ds_doc_cliente)
	into STRICT	ds_retorno_flex_w
	from	eif_escala_ii_doc
	where	nr_seq_escala = nr_seq_score_flex_p;



	if ((trim(both ds_retorno_flex_w) IS NOT NULL AND (trim(both ds_retorno_flex_w))::text <> '')) then

		ds_retorno_w := ds_retorno_flex_w;

	end if;

end if;

return substr(ds_retorno_w,1,4000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
