SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pep_obter_seq_conduta_esc ( nr_atendimento_p bigint, nr_sequencia_p bigint, ie_escala_p bigint, nr_seq_score_flex_p bigint default 0, nm_tabela_p text default null) RETURNS bigint AS $body$
DECLARE


ds_comando_w	varchar(4000);
nm_tabela_w	varchar(50);
nm_atributo_w	varchar(50);	
nr_seq_retorno_w	bigint	:= 0;	
qt_pontuacao_w		double precision;	
cd_perfil_w		bigint;
cd_setor_atendimento_w	bigint;
NR_SEQ_CONDUTA_ESC_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	PEP_CONDUTA
	where	NR_SEQ_CONDUTA_ESC	= nr_seq_conduta_esc_w
	and		qt_pontuacao_w between QT_PONTO_MIN and QT_PONTO_MAX
	and		Obter_Se_Conduta_lib(nr_sequencia,cd_perfil_w,cd_setor_atendimento_w)	= 'S';
			

BEGIN

select	max(nm_tabela),
	max(nm_atributo),
	max(nr_sequencia)
into STRICT	nm_tabela_w,
	nm_atributo_w,
	NR_SEQ_CONDUTA_ESC_w
from	pep_conduta_escala
where	ie_escala	= ie_escala_p
and ((coalesce(nr_seq_score_flex,0) = nr_seq_score_flex_p) or (coalesce(nr_seq_score_flex_ii,0) = nr_seq_score_flex_p))
and	((nm_tabela = nm_tabela_p) or (coalesce(nm_tabela_p::text, '') = '') or (nm_tabela = CASE WHEN nm_tabela_p='ESCALA_EIF' THEN 'EIF_ESCALA_RESULT'  ELSE null END ));


if (nm_tabela_w IS NOT NULL AND nm_tabela_w::text <> '') and (nm_atributo_w IS NOT NULL AND nm_atributo_w::text <> '') then
	
	
	if (nm_tabela_w = 'EIF_ESCALA_RESULT' or nm_tabela_w = 'ESCALA_EIF') then
	
		qt_pontuacao_w := somente_numero(obter_resultado_escala_eif(nr_sequencia_p,'T'));
	
	else
	
		ds_comando_w	:= 	'select	'||nm_atributo_w||
				'	from	'||nm_tabela_w||
				'	where	nr_sequencia	= :nr_sequencia	';
		qt_pontuacao_w	:= SOMENTE_NUMERO_VIRG_CHAR(Obter_select_concatenado_bv(ds_comando_w,'nr_sequencia='||nr_sequencia_p,''));	
	
	end if;	

	if (qt_pontuacao_w IS NOT NULL AND qt_pontuacao_w::text <> '') then
		cd_perfil_w	:= obter_perfil_ativo;
		cd_setor_atendimento_w	:= obter_setor_atendimento(nr_atendimento_p);
		
		open C01;
		loop
		fetch C01 into	
			nr_seq_retorno_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		end loop;
		close C01;
		
	end if;
end if;

return	nr_seq_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pep_obter_seq_conduta_esc ( nr_atendimento_p bigint, nr_sequencia_p bigint, ie_escala_p bigint, nr_seq_score_flex_p bigint default 0, nm_tabela_p text default null) FROM PUBLIC;

