CREATE OR REPLACE FUNCTION pep_obter_sexo_sbis ( nr_atendimento_p bigint, sexo_p text DEFAULT NULL) RETURNS varchar AS $body$
DECLARE

 
ie_sexo_w	varchar(10);
ie_sbis_w	varchar(1);


BEGIN 
ie_sbis_w := obter_param_usuario(0, 220, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_sbis_w);
 
IF (ie_sbis_w = 'S') THEN 
 
	SELECT	MAX(SUBSTR(obter_dados_pf_sbis(nr_atendimento_p,'SEXO'),1,255)) 
	INTO STRICT	ie_sexo_w 
	;
 
END IF;
 
RETURN	coalesce(ie_sexo_w,sexo_p);
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
