CREATE OR REPLACE FUNCTION pep_obter_se_exibe_setor ( cd_setor_atendimento_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_regras_perfil_w	bigint;
qt_regra_setor_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_regras_perfil_w
from	pep_setor_perfil
where	cd_perfil = cd_perfil_p;

if (qt_regras_perfil_w > 0) then
	select	count(*)
	into STRICT	qt_regra_setor_w
	from	pep_setor_perfil
	where	cd_perfil = cd_perfil_p
	and	cd_setor_atendimento = cd_setor_atendimento_p;

	if (qt_regra_setor_w = 0) then
		ds_retorno_w := 'S';
	else
		select	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	pep_setor_perfil
		where	cd_perfil = cd_perfil_p
		and	cd_setor_atendimento = cd_setor_atendimento_p
		and	coalesce(ie_exibe,'S') = 'S';
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
