CREATE OR REPLACE FUNCTION pep_obter_se_pac_maior_prior (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_atendimento_w		bigint;
dt_agenda_w				timestamp;
cd_agenda_w				bigint;
nr_seq_prioridade_w		bigint;
qt_reg_w				bigint;


BEGIN 
 
select	max(a.nr_atendimento), 
		max(c.dt_agenda), 
		max(c.cd_agenda), 
		max(b.nr_seq_prioridade) 
into STRICT	nr_atendimento_w, 
		dt_agenda_w, 
		cd_agenda_w, 
		nr_seq_prioridade_w 
from	atendimento_paciente a, 
		triagem_classif_prioridade b, 
		agenda_consulta_v2 c 
where	a.nr_seq_triagem_prioridade = b.nr_sequencia 
and		c.nr_atendimento = a.nr_atendimento 
and		a.ie_nivel_atencao = 'P' 
and		c.nr_sequencia = nr_seq_agenda_p 
and		c.ie_status_agenda in ('A', 'AA', 'AT', 'AC', 'AP', 'AE', 'AR');
 
select	count(*) 
into STRICT	qt_reg_w 
from	atendimento_paciente a, 
		triagem_classif_prioridade b, 
		agenda_consulta_v2 c 
where	a.nr_seq_triagem_prioridade = b.nr_sequencia 
and		c.nr_atendimento = a.nr_atendimento 
and		a.ie_nivel_atencao = 'P' 
and		a.nr_atendimento <> nr_atendimento_w 
and		c.cd_agenda = cd_agenda_w 
and		trunc(c.dt_agenda) = trunc(dt_agenda_w) 
and		b.nr_seq_prioridade < nr_seq_prioridade_w 
and		c.ie_status_agenda in ('A', 'AA', 'AT', 'AC', 'AP', 'AE', 'AR');
 
if (qt_reg_w > 0) then 
	return 'S';
else 
	return 'N';
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
