CREATE OR REPLACE FUNCTION pep_obter_se_perm_escala (ie_escala_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN
select	coalesce(max('S'),'N') ie_escala
into STRICT	ds_retorno_w
from	grupo_escala_indice_item
where	ie_escala = ie_escala_p
and	coalesce(wheb_assist_pck.getEscalaLiberada(ie_escala),'S') = 'S';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
