CREATE OR REPLACE FUNCTION pep_obter_se_regra_pastas ( nr_seq_item_pront_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) 	:= 'N';
nm_usuario_w	varchar(255)	:= wheb_usuario_pck.get_nm_usuario;


BEGIN

select 	coalesce(max('S'),'N')
into STRICT	ds_retorno_w
from   	CONFIG_WPAGE_CONTROL x
where  	coalesce(cd_perfil,cd_perfil_p)			= cd_perfil_p
and		coalesce(nm_usuario_regra,nm_usuario_w)	= nm_usuario_w
and		x.nr_seq_objeto in (	SELECT	b.NR_SEQ_DIC_OBJ
								from 	PRONTUARIO_ITEM_PASTA a,
										prontuario_pasta b
								Where	a.nr_seq_pasta 	= b.nr_sequencia
								and		nr_seq_item 	= nr_seq_item_pront_p);


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
