CREATE OR REPLACE FUNCTION pep_obter_se_visualiza ( ie_forma_restricao_p text, nm_usuario_registro_p text, nm_usuario_atual_p text) RETURNS varchar AS $body$
BEGIN

if (coalesce(ie_forma_restricao_p,'T')	= 'T') then
	return 'S';
elsif (ie_forma_restricao_p	= 'U') and (nm_usuario_registro_p	= nm_usuario_atual_p) then
	return 'S';
elsif (ie_forma_restricao_p	= 'U') and (nm_usuario_registro_p	<> nm_usuario_atual_p)  then
	return 'N';
end if;

return 'S';

end;
