CREATE OR REPLACE FUNCTION pep_se_possui_conceito_tasy (cd_node_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'X';
ie_possui_elemento_w	varchar(1) := 'N';
nr_seq_conceito_w		ehr_elemento_conceito.nr_seq_conceito%type;

c01 CURSOR FOR
SELECT  a.nr_seq_conceito
from 	ehr_elemento_conceito a,
	 conceito_elemento_tasy b
where a.nr_seq_conceito = b.nr_sequencia
and	  a.cd_node = cd_node_p;


BEGIN

if (cd_node_p IS NOT NULL AND cd_node_p::text <> '') then

	open c01;
	loop
		fetch c01 into	nr_seq_conceito_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */

		ds_retorno_w := 'S';

		select coalesce(max('S'),'N')
		into STRICT   ie_possui_elemento_w
		from   conceito_elemento_attrib
		where  nr_seq_conceito = nr_seq_conceito_w;

		if (coalesce(ie_possui_elemento_w,'N') = 'S') then
			return 'E';
		end if;

	end loop;
	close c01;


end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
