CREATE OR REPLACE FUNCTION permite_alterar_etapa_cron ( nr_seq_cronograma_p bigint, nr_seq_etapa_p bigint, qt_new_value_p bigint) RETURNS varchar AS $body$
DECLARE


qt_old_w 			double precision;
qt_hrs_contrato_w 	double precision;
qt_hrs_cronograma 	double precision;


BEGIN
	select 	a.qt_hora_prev,
			c.qt_horas_contrato
	into STRICT 	qt_hrs_cronograma,
			qt_hrs_contrato_w
	from 	proj_cron_etapa a,
			proj_cronograma c
	where 	c.nr_sequencia = a.nr_seq_cronograma
	and 	a.qt_hora_prev = (	SELECT 	max(b.qt_hora_prev)
								from 	proj_cron_etapa b
								where 	b.nr_seq_cronograma = c.nr_sequencia)
	and 	c.nr_sequencia = nr_seq_cronograma_p;

	select 	a.qt_hora_prev
	into STRICT 	qt_old_w
	from 	proj_cron_etapa a
	where 	a.nr_sequencia = nr_seq_etapa_p;

	if (((qt_new_value_p - qt_old_w)+ qt_hrs_cronograma) > qt_hrs_contrato_w) then
		return 'N';
	else
		return 'S';
	end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
