CREATE OR REPLACE FUNCTION permite_fornec_cancelar_ocie ( nr_seq_item_entrega_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_ordem_compra_w	bigint;
dt_prevista_entrega_w	timestamp;
cd_estabelecimento_w	smallint;
qt_dias_cancel_fornec_w	bigint;
ds_retorno_w		varchar(1) := 'S';


BEGIN 
 
select	nr_ordem_compra, 
	dt_prevista_entrega 
into STRICT	nr_ordem_compra_w, 
	dt_prevista_entrega_w 
from	ordem_compra_item_entrega 
where	nr_sequencia = nr_seq_item_entrega_p;
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_w;
 
select	coalesce(max(qt_dias_cancel_fornec), 0) 
into STRICT	qt_dias_cancel_fornec_w 
from	parametro_compras 
where	cd_estabelecimento	 = cd_estabelecimento_w;
 
if (qt_dias_cancel_fornec_w > 0) and 
	((trunc(clock_timestamp(),'dd') >= (trunc(dt_prevista_entrega_w, 'dd') - qt_dias_cancel_fornec_w))) then 
	ds_retorno_w	:= 'N';
end if;
 
return	ds_retorno_w;
 
end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
