CREATE OR REPLACE FUNCTION permite_fornec_cancelar_oc ( nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_estabelecimento_w	smallint;
qt_existe_w		smallint;
qt_dias_cancel_fornec_w	bigint;
ds_retorno_w		varchar(1) := 'S';


BEGIN 
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
select	coalesce(max(qt_dias_cancel_fornec), 0) 
into STRICT	qt_dias_cancel_fornec_w 
from	parametro_compras 
where	cd_estabelecimento	 = cd_estabelecimento_w;
 
select	count(*) 
into STRICT	qt_existe_w 
from	ordem_compra_item_entrega 
where	coalesce(dt_cancelamento::text, '') = '' 
and	coalesce(dt_baixa::text, '') = '' 
and	coalesce(qt_real_entrega, 0) < coalesce(qt_prevista_entrega, 0) 
and	substr(permite_fornec_cancelar_ocie(nr_sequencia),1,1) = 'N' 
and	nr_ordem_compra	= nr_ordem_compra_p;
 
if (qt_existe_w > 0) then 
	ds_retorno_w	:= 'N';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
