CREATE OR REPLACE FUNCTION permite_gerar_alta_uti (cd_setor_usuario_p bigint, cd_motivo_alta_p bigint) RETURNS varchar AS $body$
DECLARE


cd_classif_setor_w	bigint;
ie_alta_w		varchar(01)	:= 'S';
ie_obito_w		varchar(01);


BEGIN

select	max(cd_classif_setor)
into STRICT	cd_classif_setor_w
from	setor_atendimento
where	cd_setor_atendimento	= cd_setor_usuario_p;

select	max(ie_obito)
into STRICT	ie_obito_w
from	motivo_alta
where	cd_motivo_alta	= cd_motivo_alta_p;

if (cd_classif_setor_w = 4) and (ie_obito_w = 'N') then
	ie_alta_w	:= 'N';
end if;

return ie_alta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
