CREATE OR REPLACE FUNCTION permite_inserir_amamentacao (nr_atendimento_p bigint, nr_seq_nascimento_p bigint) RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w varchar(1) := 'N';	
qtd_registros_inativados_w bigint;		


BEGIN

	select count(*)
	  into STRICT qtd_registros_inativados_w
	  from nascimento_amamentacao 
	 where nr_atendimento = nr_atendimento_p
	   and ie_situacao <> 'I'
	   and nr_seq_nascimento = nr_seq_nascimento_p;
	
	if (qtd_registros_inativados_w > 0) then
	  ds_retorno_w := 'S';
	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
