CREATE OR REPLACE FUNCTION permite_justif_insp_oc ( nr_seq_regra_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_justif_regra_w	varchar(15) := 'S';
ie_permite_justif_w		varchar(1) := 'S';
qt_registros_w			bigint;


BEGIN

select	coalesce(ie_permite_justif,'S')
into STRICT	ie_permite_justif_regra_w
from	regra_diverg_ins_oc
where	nr_sequencia = nr_seq_regra_p;

if (ie_permite_justif_regra_w = 'N') then
	ie_permite_justif_w := 'N';
elsif (ie_permite_justif_regra_w = 'S') then
	ie_permite_justif_w := 'S';
elsif (ie_permite_justif_regra_w = 'D') then
	select	count(*)
	into STRICT	qt_registros_w
	from	regra_diverg_ins_oc_just
	where	nr_seq_regra = nr_seq_regra_p;

	if (qt_registros_w = 0) then
		ie_permite_justif_w := 'S';
	else
		select	count(*)
		into STRICT	qt_registros_w
		from	regra_diverg_ins_oc_just
		where	coalesce(cd_perfil_regra, cd_perfil_p)		= cd_perfil_p
		and	coalesce(cd_setor_regra, cd_setor_atendimento_p)	= cd_setor_atendimento_p
		and	coalesce(nm_usuario_regra, nm_usuario_p)		= nm_usuario_p
		and	coalesce(ie_permite_justif,'S') = 'N'
		and	nr_seq_regra = nr_seq_regra_p;

		if (qt_registros_w > 0) then
			ie_permite_justif_w := 'N';
		else
			ie_permite_justif_w := 'S';
		end if;
	end if;
end if;

return	ie_permite_justif_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
