CREATE OR REPLACE FUNCTION permite_lancar_conta ( nr_atendimento_p bigint, cd_setor_resp_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
ds_retorno_w	varchar(1)	:= 'S';
ie_fim_conta_w	varchar(1);
qt_registros_w	integer	:= 0;


BEGIN 
 
select	coalesce(ie_fim_conta,'A') 
into STRICT	ie_fim_conta_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
select	count(*) 
into STRICT	qt_registros_w 
from 	processo_atendimento 
where 	nr_atendimento	= nr_atendimento_p 
and 	cd_setor_resp	= cd_setor_resp_p 
and 	(dt_fim_real IS NOT NULL AND dt_fim_real::text <> '');
 
if (ie_fim_conta_w	= 'P') and (qt_registros_w	> 0) then 
	ds_retorno_w	:= 'N';
end if;
 
return	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
