CREATE OR REPLACE FUNCTION permitir_fechar_conta_adiant (nr_interno_conta_p text) RETURNS varchar AS $body$
DECLARE

 
ie_fecha_conta_w	varchar(01)	:= 'S';
ie_consiste_w		varchar(01)	:= 'S';
nr_atendimento_w	bigint;
cd_convenio_w		bigint;
cd_estabelecimento_w	bigint;
qt_reg_w		bigint;
		

BEGIN 
 
ie_fecha_conta_w	:= 'S';
 
select	max(nr_atendimento), 
	max(cd_convenio_parametro), 
	max(cd_estabelecimento) 
into STRICT	nr_atendimento_w, 
	cd_convenio_w, 
	cd_estabelecimento_w 
from	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
 
if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
	 
	select	coalesce(max(ie_fechar_atend_adiant),'N') 
	into STRICT	ie_consiste_w 
	from	convenio_estabelecimento 
	where	cd_estabelecimento	= cd_estabelecimento_w 
	and	cd_convenio		= cd_convenio_w;
	 
	if (ie_consiste_w	= 'S') then 
		 
		select	count(*) 
		into STRICT	qt_reg_w 
		from	adiantamento 
		where	nr_atendimento	= nr_atendimento_w 
		and	coalesce(dt_baixa::text, '') = '';
		 
		if (qt_reg_w	> 0) then 
			ie_fecha_conta_w	:= 'N';
		end if;
	end if;
	 
end if;
 
return	ie_fecha_conta_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
