CREATE OR REPLACE FUNCTION person_name_of_fml ( NR_SEQUENCIA_P PESSOA_FISICA.NR_SEQ_PERSON_NAME%type, option_p text DEFAULT 'FULLN') RETURNS varchar AS $body$
DECLARE

  ds_retorno_w varchar(60 );
  first_name   varchar(60 );
  middle_Name  varchar(60 );
  last_name    varchar(60 );

BEGIN
  IF (NR_SEQUENCIA_P IS NOT NULL AND NR_SEQUENCIA_P::text <> '')THEN
    BEGIN
      SELECT DISTINCT DS_GIVEN_NAME,
        DS_COMPONENT_NAME_1,
        DS_FAMILY_NAME
      INTO STRICT first_name,
        middle_Name,
        last_name
      FROM person_name
      WHERE NR_SEQUENCIA = NR_SEQUENCIA_P
      AND ds_type	=	'main';
      IF (option_p        = 'FN')THEN
        ds_retorno_w    := first_name;
      elsif ( option_p    = 'MN' ) THEN
        ds_retorno_w    := middle_Name;
      elsif ( option_p    = 'LN' ) THEN
        ds_retorno_w    := last_name;
      END IF;
    END;
  END IF;
RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
