CREATE OR REPLACE FUNCTION pe_obter_desc_diag ( nr_sequencia_p bigint, ie_diag_def_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
ds_diagnostico_w	varchar(2000);
ds_definicao_w		varchar(2000);


BEGIN

select	ds_diagnostico,
	ds_definicao
into STRICT	ds_diagnostico_W,
	ds_definicao_W
from	pe_diagnostico
where	nr_sequencia = nr_sequencia_p;

if (ie_diag_def_p = 'DI') then
	ds_retorno_w	:= ds_diagnostico_W;
elsif (ie_diag_def_p = 'DE') then
	ds_retorno_w	:= ds_definicao_W;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
