CREATE OR REPLACE FUNCTION pfcs_get_date_formatted (nm_usuario_p text, date_p timestamp) RETURNS varchar AS $body$
DECLARE


retorno_w	varchar(50);				
ds_locale_w	varchar(50);	
ds_language_w	varchar(50);	
cd_estabelicimento_w	bigint;			


BEGIN

	select	max(ds_locale)
	into STRICT ds_locale_w
	from	user_locale 
	where	nm_user = nm_usuario_p;

	if (coalesce(ds_locale_w::text, '') = '' )then
	select	max(cd_estabelecimento)
	into STRICT 	cd_estabelicimento_w
	from	usuario 
	where	nm_usuario = nm_usuario_p;

	select 	max(ds_locale)
	into STRICT	ds_locale_w
	from	establishment_locale 
	where	cd_estabelecimento = cd_estabelicimento_w;
end if;


	if (ds_locale_w = 'en_US' ) then
		retorno_w := to_char(date_p, 'Mon-DD-YYYY');
	else
		retorno_w := to_char(date_p, 'DD-Mon-YYYY');
	end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
